public class variable {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10;
		int b=5;
		//operators
		int c= a+b;
		System.out.println(a);
		System.out.println(b);
		System.out.println(c);
		c=a*b;
		System.out.println(c);

		String name="Tony Stark";
		System.out.println(name);
		a=b;
		System.out.println(a);
		
		a=45;
		System.out.println(a);

	}

}